  );    // // Single Listing Page    $sections[] = array(        'title'      => esc_html__( 'Single Listing Page', 'mtlisitings' ),        'id'         => 'mt_listings_settings_single',        'subsection' => true,        'fields'     => array(            array(                'id'       => 'mt_related_listings',                'type'     => 'switch',                 'title'    => esc_html__('Related Listings', 'mtlisitings'),                'subtitle' => esc_html__('Enable or disable related Listings', 'mtlisitings'),                'default'  => true,            ),        ),    );    return $sections;}// In this example OPT_NAME is the returned opt_name.add_filter("redux/options/redux_demo/sections", 'mtlisitings_extend_redux_tabs');?>